css.baseVariables:
:root {
  --bas-primary: ${primary};
  --bas-bg: ${background};
  --bas-surface: ${surface};
  --bas-text: ${text};
  --bas-radius: ${radius};
  --bas-border-width: ${borderWidth};
  --bas-outline-opacity: ${outlineOpacity};

  --bas-primary-hover: color-mix(in srgb, var(--bas-primary) 85%, black);
  --bas-primary-active: color-mix(in srgb, var(--bas-primary) 75%, black);

  --bas-text-secondary: color-mix(in srgb, var(--bas-text) 75%, #000000);
  --bas-text-disabled: color-mix(in srgb, var(--bas-text) 45%, var(--bas-surface));

  --bas-border: color-mix(in srgb, var(--bas-text) calc(15% * var(--bas-outline-opacity)), transparent);
  --bas-border-subtle: color-mix(in srgb, var(--bas-text) calc(8% * var(--bas-outline-opacity)), transparent);

  --bas-surface-elevated: color-mix(in srgb, var(--bas-surface) 95%, var(--bas-primary) 5%);
  --bas-surface-hover: color-mix(in srgb, var(--bas-text) 5%, transparent);
}

@keyframes moving-gradient {
  0% { background-position: -400px 0; }
  100% { background-position: 400px 0; }
}

@keyframes rotating-gradient {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}\n\n@keyframes spin-left {\n  0% { transform: rotate(0deg); }\n  100% { transform: rotate(-360deg); }\n}

css.popupStyles:
:root {
  --bas-primary: ${primary};
  --bas-bg: ${background};
  --bas-surface: ${surface};
  --bas-text: ${text};
  --bas-radius: ${radius};
  --bas-border-width: ${borderWidth};
  --bas-outline-opacity: ${outlineOpacity};
  --bas-primary-hover: color-mix(in srgb, var(--bas-primary) 85%, black);
  --bas-primary-active: color-mix(in srgb, var(--bas-primary) 75%, black);
  --bas-text-secondary: color-mix(in srgb, var(--bas-text) 75%, #000000);
  --bas-text-disabled: color-mix(in srgb, var(--bas-text) 45%, var(--bas-surface));
  --bas-border: color-mix(in srgb, var(--bas-text) calc(15% * var(--bas-outline-opacity)), transparent);
  --bas-border-subtle: color-mix(in srgb, var(--bas-text) calc(8% * var(--bas-outline-opacity)), transparent);
  --bas-surface-elevated: color-mix(in srgb, var(--bas-surface) 95%, var(--bas-primary) 5%);
  --bas-surface-hover: color-mix(in srgb, var(--bas-text) 5%, transparent);
}

html { background: var(--bas-surface); }
body { background: var(--bas-surface); color: var(--bas-text); border: var(--bas-border-width) solid var(--bas-border-subtle); }
.popup-header { border-bottom: var(--bas-border-width) solid var(--bas-border-subtle); background: color-mix(in srgb, var(--bas-surface) 80%, transparent); border-radius: 0; }
.header-icon-btn { background: transparent; color: var(--bas-text); border-radius: var(--bas-radius); }
.popup-header__title { color: var(--bas-text); }
.social-icon--x { fill: var(--bas-text-secondary); }
.social-icon--x:hover { fill: var(--bas-text); }
h3 { color: var(--bas-text-secondary); }
label { color: var(--bas-text-secondary); }
input[type="range"] { background: linear-gradient(to right, var(--bas-primary) 0%, var(--bas-primary) var(--slider-progress, 0%), var(--bas-border) var(--slider-progress, 0%), var(--bas-border) 100%); border-radius: calc(var(--bas-radius) / 2); }
input[type="range"]::-webkit-slider-thumb { background: var(--bas-primary); box-shadow: 0 0 0 2px var(--bas-surface); border-radius: 50%; }
input[type="range"]::-webkit-slider-thumb:hover { background: var(--bas-primary-hover); }
.setting__slider-value { background: var(--bas-surface); color: var(--bas-text); border: var(--bas-border-width) solid var(--bas-border-subtle); border-radius: var(--bas-radius); }
textarea { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); color: var(--bas-text); border-radius: var(--bas-radius); }
.tool { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); border-radius: var(--bas-radius); }
.tool:hover { background: var(--bas-surface-elevated); border-color: var(--bas-border); }
.tool.active { background: var(--bas-surface-elevated); border-color: var(--bas-primary); box-shadow: 0 0 0 1px color-mix(in srgb, var(--bas-primary) 20%, transparent); }
.tool__name { color: var(--bas-text); }
.tool__toggle { background: var(--bas-border); position: relative; border-radius: 9999px; }
.tool__toggle::after { background: var(--bas-bg); box-shadow: 0 1px 3px color-mix(in srgb, var(--bas-text) 20%, transparent); border-radius: 50%; }
.tool.active .tool__toggle { background: var(--bas-primary); }
.tool.active .tool__toggle::after { background: var(--bas-bg); }
button { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); border: none; }

button:disabled { background: var(--bas-border); color: var(--bas-text-disabled); }
#add-new-preset-btn { background: var(--bas-surface); color: var(--bas-text); border: 2px solid var(--bas-border); border-radius: var(--bas-radius); }
#add-new-preset-btn:hover:not(:disabled) { background: var(--bas-surface-elevated); border-color: var(--bas-primary); }
.preset { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); border-radius: var(--bas-radius); }
.preset:hover { background: var(--bas-surface-elevated); border-color: var(--bas-border); }
.preset.selected { background: var(--bas-surface-elevated); border-color: var(--bas-primary); box-shadow: 0 0 0 1px color-mix(in srgb, var(--bas-primary) 20%, transparent); }
.preset__name { color: var(--bas-text); }
.preset__action-button { background: transparent; color: var(--bas-text-secondary); border-radius: var(--bas-radius); }
.preset__action-button:hover { color: var(--bas-text); background: color-mix(in srgb, var(--bas-text) 10%, transparent); }
.preset__action-button--delete:hover { color: var(--bas-primary); background: color-mix(in srgb, var(--bas-primary) 10%, transparent); }
.preset__reorder-button { background: transparent; color: var(--bas-text); border-radius: var(--bas-radius); }
.preset__reorder-button:disabled { color: var(--bas-text-secondary); }
.preset__name-input { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); color: var(--bas-text); border-radius: var(--bas-radius); }
.modal { background: rgba(0, 0, 0, 0.8); }
.modal__content { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.4); border-radius: var(--bas-radius); }
.modal__content h4 { color: var(--bas-text); }
.modal__content p { color: var(--bas-text-secondary); }
.modal__content input { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); color: var(--bas-text); border-radius: var(--bas-radius); }
.modal__content input::placeholder { color: var(--bas-text-secondary); }
#delete-confirm-modal-cancel-btn, #save-preset-modal-cancel-btn { background: var(--bas-surface); color: var(--bas-text); border: var(--bas-border-width) solid var(--bas-border); border-radius: var(--bas-radius); }
#delete-confirm-modal-cancel-btn:hover:not(:disabled), #save-preset-modal-cancel-btn:hover:not(:disabled) { background: var(--bas-surface-elevated); }
#delete-confirm-modal-confirm-btn { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); }
#delete-confirm-modal-confirm-btn:hover:not(:disabled) { background: var(--bas-primary-hover); }
#save-preset-modal-save-btn { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); }
#save-preset-modal-save-btn:hover:not(:disabled) { background: var(--bas-primary-hover); }
#no-presets-msg { color: var(--bas-text-secondary); }
.theme-modal-overlay { background: rgba(0, 0, 0, 0.85); }
.theme-modal { background: var(--bas-surface); border-radius: 0; }
.modal-header { border-bottom: var(--bas-border-width) solid var(--bas-border); background: var(--bas-surface); border-radius: 0; }
.modal-title { color: var(--bas-text); }
.modal-close-btn { background: transparent; color: var(--bas-text-secondary); border-radius: var(--bas-radius); }
.modal-close-btn:hover { color: var(--bas-text); background: var(--bas-surface-hover); }
.modal-content::-webkit-scrollbar-thumb { background: var(--bas-border); border-radius: calc(var(--bas-radius) / 2); }
.nav-tab { background: transparent; color: var(--bas-text-secondary); border-radius: var(--bas-radius); }
.nav-tab:hover:not(.disabled) { color: var(--bas-text); background: var(--bas-surface-hover); }
.nav-tab.active { color: var(--bas-primary); background: color-mix(in srgb, var(--bas-primary) 10%, transparent); }
.coming-soon { background: var(--bas-surface-hover); color: var(--bas-primary); border-radius: calc(var(--bas-radius) / 2); }
.section-title { color: var(--bas-text); }
.count { color: var(--bas-text-secondary); }
.empty-msg { color: var(--bas-text-secondary); }
.theme-card { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); border-radius: var(--bas-radius); }
.theme-card:hover { border-color: var(--bas-primary); box-shadow: 0 4px 12px color-mix(in srgb, var(--bas-primary) 25%, transparent); background: var(--bas-surface-hover); }
.theme-card.active { border-color: var(--bas-primary); background: color-mix(in srgb, var(--bas-primary) 8%, transparent); box-shadow: 0 0 0 2px color-mix(in srgb, var(--bas-primary) 20%, transparent); }
.theme-name { color: var(--bas-text); }
.theme-actions button { background: transparent; color: var(--bas-text-secondary); border-radius: var(--bas-radius); }
.theme-actions button:hover { color: var(--bas-text); background: var(--bas-surface-hover); }
.theme-fav.favorited { color: var(--bas-primary); }
.theme-delete:hover { color: var(--bas-primary); }
.swatch { border: var(--bas-border-width) solid var(--bas-border); border-radius: calc(var(--bas-radius) / 2); }
.theme-tag { background: var(--bas-surface-hover); color: var(--bas-text-secondary); border-radius: calc(var(--bas-radius) / 2); }
.editor-section { border-top: 2px solid var(--bas-border); }
.editor-mode-tabs { background: var(--bas-surface); border-radius: var(--bas-radius); }
.mode-tab { background: transparent; color: var(--bas-text-secondary); border-radius: var(--bas-radius); }
.mode-tab:hover { color: var(--bas-text); }
.mode-tab.active { background: var(--bas-primary); color: var(--bas-bg); }
.editor-main { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); border-radius: var(--bas-radius); }
.editor-main::-webkit-scrollbar-thumb { background: var(--bas-border); border-radius: calc(var(--bas-radius) / 2); }
.color-item label { color: var(--bas-text-secondary); }
.color-input-group input[type=color] { border: var(--bas-border-width) solid var(--bas-border); background: var(--bas-surface); border-radius: var(--bas-radius); }
.hex-input { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); color: var(--bas-text); border-radius: var(--bas-radius); }
.hex-input:focus { border-color: var(--bas-primary); }
.advanced-section h4 { color: var(--bas-text-secondary); }
.color-item-compact label { color: var(--bas-text-secondary); }
.setting-item label { color: var(--bas-text-secondary); }
.input-with-unit { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); border-radius: var(--bas-radius); }
.input-with-unit input { background: transparent; color: var(--bas-text); }
.input-with-unit span { color: var(--bas-text-secondary); border-left: var(--bas-border-width) solid var(--bas-border); }
.editor-preview { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); border-radius: var(--bas-radius); }
.preview-label { color: var(--bas-text-secondary); }
.theme-name-input { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); color: var(--bas-text); border-radius: var(--bas-radius); }
.theme-name-input:focus { border-color: var(--bas-primary); }
.btn-primary { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); }
.btn-primary:hover { background: var(--bas-primary-hover); }
.btn-secondary { background: var(--bas-surface); color: var(--bas-text); border-radius: var(--bas-radius); }
.btn-secondary:hover { background: var(--bas-surface-elevated); }
.coming-soon-message { color: var(--bas-text-secondary); }
.theme-card:focus-visible, button:focus-visible, input:focus-visible { outline: 2px solid var(--bas-primary); outline-offset: 2px; }
.switch-label { background-color: var(--bas-border); border: none; border-radius: 9999px; }
.checkbox:checked ~ .slider { background-color: var(--bas-primary); }
.slider { border-radius: 9999px; }
.slider::before { box-shadow: 0 1px 3px color-mix(in srgb, var(--bas-text) 20%, transparent); background-color: var(--bas-bg); border-radius: 50%; }
.checkbox:checked ~ .slider::before { background-color: var(--bas-bg); }
.starred-section .section-title { color: var(--bas-text); }
.starred-section .section-title::before { color: var(--bas-primary); }
.theme-customizer__value-input {
  min-width: 32px;
  width: 45px;
  font-size: 11px;
  font-weight: 600;
  text-align: right;
  color: var(--bas-text);
  background: var(--bas-surface);
  border: var(--bas-border-width) solid var(--bas-border-subtle);
  border-radius: var(--bas-radius);
  padding: 2px 4px;
  font-family: inherit;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
  box-sizing: border-box;
}
.theme-customizer__value-input:focus {
  outline: none;
  border-color: var(--bas-primary);
  box-shadow: 0 0 0 1px color-mix(in srgb, var(--bas-primary) 20%, transparent);
}

