/* Responsive logo text sizing */
@media (max-width: 768px) {
  .navbar-header .bas-logo-text,
  .logo-title-wrapper .bas-logo-text,
  .nav-content .bas-logo-text {
    font-size: 1.4rem !important;
  }
  .title-content-container .bas-logo-text,
  .main-content-container .bas-logo-text,
  .title-container-v3 .bas-logo-text {
    font-size: 2.8rem !important;
  }
  /* Ensure split text parts inherit responsive sizing */
  .navbar-header .bas-logo-text__better,
  .navbar-header .bas-logo-text__ai-studio,
  .logo-title-wrapper .bas-logo-text__better,
  .logo-title-wrapper .bas-logo-text__ai-studio,
