.theme-customizer__value-input {
  min-width: 32px;
  width: 45px;
  font-size: 11px;
  font-weight: 600;
  text-align: right;
  color: var(--bas-text);
  background: var(--bas-surface);
  border: var(--bas-border-width) solid var(--bas-border-subtle);
  border-radius: var(--bas-radius);
  padding: 2px 4px;
  font-family: inherit;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
  box-sizing: border-box;
}
.theme-customizer__value-input:focus {
  outline: none;
  border-color: var(--bas-primary);
  box-shadow: 0 0 0 1px color-mix(in srgb, var(--bas-primary) 20%, transparent);
}
