css.baseVariables:
:root {
  --bas-primary: ${primary};
  --bas-bg: ${background};
  --bas-surface: ${surface};
  --bas-text: ${text};
  --bas-radius: ${radius};
  --bas-border-width: ${borderWidth};

  --bas-primary-hover: color-mix(in srgb, var(--bas-primary) 85%, black);
  --bas-primary-active: color-mix(in srgb, var(--bas-primary) 75%, black);

  --bas-text-secondary: color-mix(in srgb, var(--bas-text) 75%, #000000);
  --bas-text-disabled: color-mix(in srgb, var(--bas-text) 45%, var(--bas-surface));

  --bas-border: color-mix(in srgb, var(--bas-text) 15%, transparent);
  --bas-border-subtle: color-mix(in srgb, var(--bas-text) 8%, transparent);

  --bas-surface-elevated: color-mix(in srgb, var(--bas-surface) 95%, var(--bas-primary) 5%);
  --bas-surface-hover: color-mix(in srgb, var(--bas-text) 5%, transparent);
}

@keyframes moving-gradient {
  0% { background-position: -400px 0; }
  100% { background-position: 400px 0; }
}

@keyframes rotating-gradient {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

css.popupStyles:
:root {
  --bas-primary: ${primary};
  --bas-bg: ${background};
  --bas-surface: ${surface};
  --bas-text: ${text};
  --bas-radius: ${radius};
  --bas-border-width: ${borderWidth};
  --bas-primary-hover: color-mix(in srgb, var(--bas-primary) 85%, black);
  --bas-primary-active: color-mix(in srgb, var(--bas-primary) 75%, black);
  --bas-text-secondary: color-mix(in srgb, var(--bas-text) 75%, #000000);
  --bas-text-disabled: color-mix(in srgb, var(--bas-text) 45%, var(--bas-surface));
  --bas-border: color-mix(in srgb, var(--bas-text) 15%, transparent);
  --bas-border-subtle: color-mix(in srgb, var(--bas-text) 8%, transparent);
  --bas-surface-elevated: color-mix(in srgb, var(--bas-surface) 95%, var(--bas-primary) 5%);
  --bas-surface-hover: color-mix(in srgb, var(--bas-text) 5%, transparent);
}

html { background: var(--bas-surface); }
body { background: var(--bas-surface); color: var(--bas-text); border: var(--bas-border-width) solid var(--bas-border-subtle); }
.popup-header { border-bottom: var(--bas-border-width) solid var(--bas-border-subtle); background: color-mix(in srgb, var(--bas-surface) 80%, transparent); border-radius: 0; }
.header-icon-btn { background: transparent; color: var(--bas-text); border-radius: var(--bas-radius); }
.popup-header__title { color: var(--bas-text); }
.social-icon--x { fill: var(--bas-text-secondary); }
.social-icon--x:hover { fill: var(--bas-text); }
h3 { color: var(--bas-text-secondary); }
label { color: var(--bas-text-secondary); }
input[type="range"] { background: linear-gradient(to right, var(--bas-primary) 0%, var(--bas-primary) var(--slider-progress, 0%), var(--bas-border) var(--slider-progress, 0%), var(--bas-border) 100%); border-radius: calc(var(--bas-radius) / 2); }
input[type="range"]::-webkit-slider-thumb { background: var(--bas-primary); box-shadow: 0 0 0 2px var(--bas-surface); border-radius: 50%; }
input[type="range"]::-webkit-slider-thumb:hover { background: var(--bas-primary-hover); }
.setting__slider-value { background: var(--bas-surface); color: var(--bas-text); border: var(--bas-border-width) solid var(--bas-border-subtle); border-radius: var(--bas-radius); }
textarea { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); color: var(--bas-text); border-radius: var(--bas-radius); }
.tool { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); border-radius: var(--bas-radius); }
.tool:hover { background: var(--bas-surface-elevated); border-color: var(--bas-border); }
.tool.active { background: var(--bas-surface-elevated); border-color: var(--bas-primary); box-shadow: 0 0 0 1px color-mix(in srgb, var(--bas-primary) 20%, transparent); }
.tool__name { color: var(--bas-text); }
.tool__toggle { background: var(--bas-border); position: relative; border-radius: 9999px; }
.tool__toggle::after { background: var(--bas-bg); box-shadow: 0 1px 3px color-mix(in srgb, var(--bas-text) 20%, transparent); border-radius: 50%; }
.tool.active .tool__toggle { background: var(--bas-primary); }
.tool.active .tool__toggle::after { background: var(--bas-bg); }
button { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); border: none; }

button:disabled { background: var(--bas-border); color: var(--bas-text-disabled); }
#add-new-preset-btn { background: var(--bas-surface); color: var(--bas-text); border: 2px solid var(--bas-border); border-radius: var(--bas-radius); }
#add-new-preset-btn:hover:not(:disabled) { background: var(--bas-surface-elevated); border-color: var(--bas-primary); }
.preset { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); border-radius: var(--bas-radius); }
.preset:hover { background: var(--bas-surface-elevated); border-color: var(--bas-border); }
.preset.selected { background: var(--bas-surface-elevated); border-color: var(--bas-primary); box-shadow: 0 0 0 1px color-mix(in srgb, var(--bas-primary) 20%, transparent); }
.preset__name { color: var(--bas-text); }
.preset__action-button { background: transparent; color: var(--bas-text-secondary); border-radius: var(--bas-radius); }
.preset__action-button:hover { color: var(--bas-text); background: color-mix(in srgb, var(--bas-text) 10%, transparent); }
.preset__action-button--delete:hover { color: var(--bas-primary); background: color-mix(in srgb, var(--bas-primary) 10%, transparent); }
.preset__reorder-button { background: transparent; color: var(--bas-text); border-radius: var(--bas-radius); }
.preset__reorder-button:disabled { color: var(--bas-text-secondary); }
.preset__name-input { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); color: var(--bas-text); border-radius: var(--bas-radius); }
.modal { background: rgba(0, 0, 0, 0.8); }
.modal__content { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border); box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.4); border-radius: var(--bas-radius); }
.modal__content h4 { color: var(--bas-text); }
.modal__content p { color: var(--bas-text-secondary); }
.modal__content input { background: var(--bas-surface); border: var(--bas-border-width) solid var(--bas-border-subtle); color: var(--bas-text); border-radius: var(--bas-radius); }
.modal__content input::placeholder { color: var(--bas-text-secondary); }
#delete-confirm-modal-cancel-btn, #save-preset-modal-cancel-btn { background: var(--bas-surface); color: var(--bas-text); border: var(--bas-border-width) solid var(--bas-border); border-radius: var(--bas-radius); }
#delete-confirm-modal-cancel-btn:hover:not(:disabled), #save-preset-modal-cancel-btn:hover:not(:disabled) { background: var(--bas-surface-elevated); }
#delete-confirm-modal-confirm-btn { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); }
#delete-confirm-modal-confirm-btn:hover:not(:disabled) { background: var(--bas-primary-hover); }
#save-preset-modal-save-btn { background: var(--bas-primary); color: var(--bas-bg); border-radius: var(--bas-radius); }
#save-preset-modal-save-btn:hover:not(:disabled) { background: var(--bas-primary-hover); }
#no-presets-msg { color: var(--bas-text-secondary); }
.theme-modal-overlay { background: rgba(0, 0, 0, 0.85); }
.theme-modal { background: var(--bas-surface); border-radius: 0; }
.modal-header { border-bottom: var(--bas-border-width) solid var(--bas-border); background: var(--bas-surface); border-radius: 0; }
